CREATE TABLE T_USER( --用户表
	ID CHAR(32 BYTE), 
	LOGINNAME VARCHAR2(50 BYTE), 
	PWD VARCHAR2(50 BYTE), 
	NAME VARCHAR2(50 BYTE), 
	CREATETIME DATE, 
	SEX VARCHAR2(20 BYTE), 
	MONEY NUMBER(10,2) DEFAULT 10000000, 
	FACE VARCHAR2(255 BYTE) DEFAULT 'face01.jpg',
	TAG NUMBER DEFAULT 0
);

--管理员数据


CREATE TABLE T_ADDRESS( --送货地址表
	ID CHAR(32 BYTE), 
	AREA VARCHAR2(200 BYTE), 
	AREADES VARCHAR2(200 BYTE), 
	TEL VARCHAR2(20 BYTE), 
	USERID CHAR(32 BYTE), 
	RECEIVER VARCHAR2(20 BYTE), 
	CREATETIME DATE DEFAULT SYSDATE
);

CREATE TABLE T_GOODS ( --商品表
	ID CHAR(32 BYTE), 
	NAME VARCHAR2(255 BYTE), 
	PRICE NUMBER(10,2), 
	SALES NUMBER, 
	TATE NUMBER DEFAULT 0, 
	BIGIMGPATH VARCHAR2(255 BYTE), 
	CREATETIME DATE DEFAULT SYSDATE, 
	TYPEMENUID CHAR(32 BYTE), 
	DES VARCHAR2(500 BYTE), 
	NUM NUMBER DEFAULT 0, 
	USER_ID CHAR(32 BYTE)
);

--默认商品数据


CREATE TABLE T_SMALLIMG( --商品图片表
	ID CHAR(32 BYTE), 
	SRC VARCHAR2(255 BYTE), 
	URL VARCHAR2(255 BYTE), 
	GOODSID CHAR(32 BYTE)
);
--商品图片数据

CREATE TABLE T_COMMENT(	--评论表
	ID CHAR(32 BYTE), 
	CONTENT VARCHAR2(800 BYTE), 
	GRADE NUMBER, 
	CREATETIME DATE DEFAULT SYSDATE, 
	USERID CHAR(32 BYTE), 
	GOODSID CHAR(32 BYTE)
);
 
CREATE TABLE T_COMMENTIMG( --评论图片表
	ID CHAR(32 BYTE), 
	URL VARCHAR2(200 BYTE), 
	COMMENTID CHAR(32 BYTE), 
	CREATETIME DATE DEFAULT SYSDATE
);

CREATE TABLE T_TYPEMENU( --一级分类菜单
	ID CHAR(32 BYTE), 
	MENU VARCHAR2(255 BYTE), 
	URL VARCHAR2(255 BYTE), 
	NO VARCHAR2(20 BYTE), 
	CREATETIME DATE DEFAULT SYSDATE
);


CREATE TABLE T_TYPEITEMTITLE( --二级分类菜单
	ID CHAR(32 BYTE), 
	ITEMTITLE VARCHAR2(255 BYTE), 
	URL VARCHAR2(255 BYTE), 
	TYPEMENUID CHAR(32 BYTE)
);


CREATE TABLE T_TYPEITEM( --三级分类菜单
	ID CHAR(32 BYTE), 
	ITEM VARCHAR2(255 BYTE), 
	URL VARCHAR2(255 BYTE), 
	TYPEMENUTITLEID CHAR(32 BYTE)
);


CREATE TABLE T_CART( --购物车表
	ID CHAR(32 BYTE), 
	GOODSID CHAR(32 BYTE), 
	USERID CHAR(32 BYTE), 
	GOODSNUM NUMBER, 
	CREATETIME DATE DEFAULT SYSDATE, 
	CHECKED NUMBER DEFAULT 1
);

CREATE TABLE T_ORDER( --订单表
	ID CHAR(32 BYTE), 
	ORDERNUM VARCHAR2(100 BYTE), 
	BEGINTIME DATE DEFAULT SYSDATE, 
	ENDTIME DATE, 
	STATUS NUMBER DEFAULT 1, 
	TOTALMONEY NUMBER(20,2), 
	PAYWAY VARCHAR2(20 BYTE), 
	USERID CHAR(32 BYTE), 
	USERNAME VARCHAR2(50 BYTE), 
	ADDRESSID CHAR(32 BYTE)
);

CREATE TABLE T_ORDER_GOODS( --订单商品关联表
	ID CHAR(32 BYTE), 
	ORDERID CHAR(32 BYTE), 
	GOODSID CHAR(32 BYTE), 
	GOODSNUM NUMBER(10,0)
);

--文件存放列表
CREATE TABLE T_FILELISTS(
  ID CHAR(32) PRIMARY KEY, --文件id
  USERID CHAR(32) NOT NULL, --用户id
  FILENAME VARCHAR(500), --文件名
  FILESIZE VARCHAR(500), --文件大小
  FILETYPE VARCHAR(50), --文件类型
  CREATETIME DATE DEFAULT SYSDATE --上传时间
);
